mapper.update:
UPDATE SHOPPING_QUESTION_BOARD
SET QUESTION_TITLE = #{questionTitle}
	, QUESTION_CONTENT= #{questionContent}
WHERE QUESTION_NUMBER = #{questionNumber}

UPDATE SHOPPING_QUESTION_BOARD
SET QUESTION_DELETE_CHECK = 'Y'
WHERE QUESTION_NUMBER = #{questionNumber}

mapper.select:
SELECT COUNT(*) FROM SHOPPING_QUESTION_BOARD WHERE QUESTION_DELETE_CHECK = 'N'
AND ${searchSelect} LIKE '%'||#{searchKeyword}||'%'
AND QUESTION_REGDATE BETWEEN #{startDate} AND #{endDate}

SELECT TO_NUMBER(#{startQuestionNumber} -1 + rownum) AS rowNumber , QUESTION_NUMBER , QUESTION_USER , QUESTION_TITLE , QUESTION_CONTENT , QUESTION_REGDATE , QUESTION_MODIFY_REGDATE , QUESTION_DELETE_CHECK FROM( SELECT ROWNUM RN , QUESTION_NUMBER , QUESTION_USER , QUESTION_TITLE , QUESTION_CONTENT , QUESTION_REGDATE , QUESTION_MODIFY_REGDATE , QUESTION_DELETE_CHECK FROM ( SELECT QUESTION_NUMBER , QUESTION_USER , QUESTION_TITLE , QUESTION_CONTENT , QUESTION_REGDATE , QUESTION_MODIFY_REGDATE , QUESTION_DELETE_CHECK FROM SHOPPING_QUESTION_BOARD WHERE QUESTION_DELETE_CHECK = 'N'
AND ${searchSelect} LIKE '%'||#{searchKeyword}||'%'
AND QUESTION_REGDATE BETWEEN #{startDate} AND #{endDate}
ORDER BY QUESTION_NUMBER DESC ) ) WHERE RN BETWEEN #{startQuestionNumber} and #{endQuestionNumber}
